mapper.select:
select
menu_id, menu_name, parent_id, order_num, url, menu_type, visible, perms, icon, create_by, create_time,
update_by, update_time, remark
from trademark.tbl_menu


        and menu_id = #{menuId}


        and menu_name = #{menuName}


        and parent_id = #{parentId}


        and order_num = #{orderNum}


        and url = #{url}


        and menu_type = #{menuType}


        and visible = #{visible}


        and perms = #{perms}


        and icon = #{icon}


        and create_by = #{createBy}


        and create_time = #{createTime}


        and update_by = #{updateBy}


        and update_time = #{updateTime}


        and remark = #{remark}

select distinct tm.* from tbl_menu tm inner join tbl_role_menu trm on tm.menu_id = trm.menu_id where trm.role_id in (select tur.role_id from tbl_user tu inner join tbl_user_role tur on tu.user_id = tur.user_id
and tu.login_name = #{loginName}
and tm.visible=0
)

mapper.update:
update trademark.tbl_menu
set visible=1
where menu_id = #{menuId}
